<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Pelanggan</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-success" onClick="showFormAddNew()"><i class="icon-plus icon-white"></i> Tambah Baru</button>			</div>			<br><br>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th width='10%'>ID Pelanggan</th>						<th>Nama Lengkap</th>						<th>Alamat</th>						<th>Telepon</th>						<th width='15%'>Aksi</th>					</tr>				</thead>								<tbody id="tblCostumer">					<? foreach ($this->listCostumer as $key => $value) { ?>						<tr id="costumer-<? echo $value['id']; ?>">							<td>								<? echo $value['code']; ?>								<input type="hidden" name="code-<? echo $value['id']; ?>" value="<? echo $value['code']; ?>">							</td>							<td>								<? echo $value['name']; ?>								<input type="hidden" name="name-<? echo $value['id']; ?>" value="<? echo $value['name']; ?>">							</td>							<td>								<? echo $value['address']; ?>								<input type="hidden" name="address-<? echo $value['id']; ?>" value="<? echo $value['address']; ?>">							</td>							<td>								<? echo $value['phone']; ?>								<input type="hidden" name="phone-<? echo $value['id']; ?>" value="<? echo $value['phone']; ?>">							</td>							<td>								<div class="btn-group">									<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return showFormUpdate(<? echo $value['id']; ?>)">											<i class="icon-pencil"></i> Update</a>										</li>										<li>											<a href="javascript:void(0)" onClick="return hapus(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><!-- Form Add Costumer [modal]===================================== --><div id="formAddNew" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Tambah Pelanggan</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputName">Nama Pelanggan</label>				<div class="controls">					<input name="inputName" class="span3" type="text">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputAddress">Alamat</label>				<div class="controls">					<textarea name="inputAddress"></textarea>				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputPhone">Telepon</label>				<div class="controls">					<input name="inputPhone" class="span2" type="text">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="addNew()" data-dismiss="modal" aria-hidden="true">Simpan</button>	</div></div><!-- End of Form Add Costumer [modal] --><!-- Form Update Costumer [modal]===================================== --><div id="formUpdate" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Update Pelanggan</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputCode">ID Pelanggan</label>				<div class="controls">					<input name="inputID" type="hidden">					<input name="inputCode" class="span1" type="text" disabled>				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputUName">Nama Pelanggan</label>				<div class="controls">					<input name="inputUName" class="span3" type="text">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputUAddress">Alamat</label>				<div class="controls">					<textarea name="inputUAddress"></textarea>				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputUPhone">Telepon</label>				<div class="controls">					<input name="inputUPhone" class="span2" type="text">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="update()" data-dismiss="modal" aria-hidden="true">Simpan</button>	</div></div><!-- End of Form Update Costumer [modal] --><script type="text/javascript">	$(function(){		$('#inputBirthDate').datepicker({format: 'yyyy/mm/dd'});		$('#inputUBirthDate').datepicker({format: 'yyyy/mm/dd'});	})	function showFormAddNew()	{		$('#formAddNew').modal('show');		$('input[name=inputName]').val('');		$('textarea[name=inputAddress]').val('');		$('input[name=inputPhone]').val('');	}	function showFormUpdate(id)	{		$('#formUpdate').modal('show');		$('input[name=inputID]').val(id);		$('input[name=inputCode]').val($('input[name=code-'+id+']').val());		$('input[name=inputUName]').val($('input[name=name-'+id+']').val());		$('textarea[name=inputUAddress]').val($('input[name=address-'+id+']').val());		$('input[name=inputUPhone]').val($('input[name=phone-'+id+']').val());	}	function addNew()	{		var name = $('input[name=inputName]').val();		var address = $('textarea[name=inputAddress]').val();		var phone = $('input[name=inputPhone]').val();		$.post(site+'costumer/addnew', 			{name : name, address : address, phone : phone}, 			function() {				window.location = site+"costumer";		});	}	function update()	{		var id = $('input[name=inputID]').val();		var name = $('input[name=inputUName]').val();		var address = $('textarea[name=inputUAddress]').val();		var phone = $('input[name=inputUPhone]').val();		$.post(site+'Costumer/update', 			{id : id, name : name, address : address, phone : phone}, 			function(data) {				window.location = site+"costumer";		},'json');	}	function hapus(id)	{		if(confirm("Anda Yakin Akan Menghapus Data Ini?"))		{			$.post(site+'costumer/delete', {id : id}, function(){				$('#costumer-'+id).remove();			});		}	}</script>