<? foreach ($this->listPlacement as $key => $value) { ?>	<tr id="placement-<? echo $value['id']; ?>">		<td>			<? echo $value['name']; ?>			<input type="hidden" name="place-<? echo $value['id']; ?>" value="<? echo $value['place']; ?>">		</td>		<td>			<a href="#" onClick="load('product/details/'+<? echo $value['productid']; ?>, '#content');"><? echo $value['title']; ?></a>			<input type="hidden" name="product-<? echo $value['id']; ?>" value="<? echo $value['product']; ?>">			<input type="hidden" name="title-<? echo $value['id']; ?>" value="<? echo $value['title']; ?>">		</td>		<td>			<div class="btn-group">				<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return showFormUpdatePlacement(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Perbarui</a>					</li>					<li>						<a href="javascript:void(0)" onClick="return deletePlacement(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Hapus</a>					</li>				</ul>			</div>		</td>	</tr><? } ?>