<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>product">Data Produk</a></h2>		</div>		<div class="well">			<form class="form-horizontal">			<legend>Detail Buku / Produk</legend>				<div class="control-group">					<label class="control-label" for="inputTitle">Judul</label>					<div class="controls">						<input type="text" name="inputTitle" class="span9">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputPrice">Harga Pokok</label>					<div class="controls">						<input type="text" name="inputPrice" class="span3" onKeyUp="formatNumber(this)">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSellPrice">Harga Jual</label>					<div class="controls">						<input type="text" name="inputSellPrice" class="span3" onKeyUp="formatNumber(this)">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputStock">Jumlah Stok Awal Penginputan</label>					<div class="controls">						<input type="text" name="inputStock" class="span2">					</div>				</div>			<legend>Klasifikasi</legend>				<div class="control-group">					<label class="control-label" for="inputClass">Klasifikasi</label>					<div class="controls">						<select name="inputClass" class="span5">							<option value="0">--Pilih</option>							<? foreach ($this->listClass as $key => $value) { ?>								<option value="<? echo $value['id']; ?>"><? echo $value['name']; ?></option>							<? } ?>						</select>					</div>				</div>			<legend>Supplier</legend>				<div class="control-group">					<label class="control-label" for="inputConsignment">Jenis Produk</label>					<div class="controls">						<select name="inputConsignment" class="span5">							<option value="0">Milik Sendiri</option>							<option value="1">Konsinyasi</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSupplier">Supplier</label>					<div class="controls">						<input type="hidden" id="inputSupplierID" value="0">						<input type="text" id="inputSupplierName" class="span5" data-provide="typeahead" placeholder="Cari Supplier">					</div>				</div>			<legend>Penerbit</legend>				<div class="control-group">					<label class="control-label" for="inputPublisher">Penerbit</label>					<div class="controls">						<input type="hidden" id="inputPublisherID">						<input type="text" id="inputPublisherName" class="span5" data-provide="typeahead" placeholder="Cari Penerbit">					</div>				</div>			<legend>Penulis</legend>				<div class="control-group">					<label class="control-label" for="inputWriter">Penulis</label>					<div class="controls">						<input type="text" id="inputWriterName" class="span7">					</div>				</div>			</form>			<br><br>			<div class="info"></div>			<div class="pull-right"><button class="btn btn-success" onClick="saveData()">Tambahkan Produk Baru</button></div>			<br><br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputPublisherName').typeahead({			source : function(query,process){				return $.post(site+'product/xlistpublisher', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].name);					}					return process(sourceArr);				})			},			highlighter : function(item){				var publisher = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/printer.png'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + publisher[1] + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var publisher = item.split('#');				$('#inputPublisherID').val(publisher[0]);				return publisher[1];							}		});		$('#inputSupplierName').typeahead({			source : function(query,process){				return $.post(site+'product/xlistsupplier', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].name);					}					return process(sourceArr);				})			},			highlighter : function(item){				var supplier = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/suppliers_icon.gif'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + supplier[1] + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var supplier = item.split('#');				$('#inputSupplierID').val(supplier[0]);				return supplier[1];							}		});	});	function saveData()	{		var title = $('input[name=inputTitle]').val();		var price = parseInt(replaceAll($('input[name=inputPrice]').val(),',',''));		var sellprice = parseInt(replaceAll($('input[name=inputSellPrice]').val(),',',''));		var stock = $('input[name=inputStock]').val();		var classes = $('select[name=inputClass]').val();		var publisher = $('#inputPublisherID').val();		var writer = $('#inputWriterName').val();		var supplier = $('#inputSupplierID').val();		var consignment = $('select[name=inputConsignment]').val();		if(title == '' || classes == 0 || supplier == 0)		{			$('.info').html("<div class='alert alert-error'><h4>Upppss!!</h4>Mohon Isi dengan lengkap..</div>");		}		else		{			$.post(site+'product/addnew',				{title : title,				writer : writer,				price : price,				sellprice : sellprice,				stock : stock,				classes : classes,				publisher : publisher,				supplier : supplier,				consignment : consignment}, 				function(){					window.location = site+"product";				});		}	}</script>