<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">		<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>report/loan">Laporan Piutang</a></h2>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="load('report/loanbystatus/'+1,'#printArea')"><i class="icon-filter icon-white"></i> Lunas</button>				<button class="btn btn-danger" onClick="load('report/loanbystatus/'+0,'#printArea')"><i class="icon-filter icon-white"></i> Belum Lunas</button>			</div>				<input id="inputSearching" class="span5" type="text" data-provide="typeahead" placeholder="Ketikkan Kode Piutang atau Nama Pelanggan">						<div id="printArea">				<table style="border:none; width:100%;">					<tr>						<th colspan="3"><center>LAPORAN PIUTANG</center></th>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td width:'40%'>							<p>								TOKO BUKU TOHA PUTRA<br>								Cabang MAKASSAR<br>								Jl. Sultan Alauddin No. 18-19 Komp. Ruko Permata Sari<br>								Telp : 0411-868601, FAX : 0411-868177<br>							</p>						</td>						<td width:'20%'></td>						<td width:'40%'></td>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td></td>						<td></td>						<td><small class="pull-right">Tanggal Cetak : <? echo date('d-m-Y'); ?></small></td>					</tr>				</table>				<table class="table table-bordered">					<thead>						<tr>							<th width='15%' style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Nomor Tagihan</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Pelanggan</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Deadline</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Status</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Total Tagihan</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Terbayar</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Sisa Tagihan</th>						</tr>					</thead>									<tbody>					<? foreach ($this->listLoan as $key => $value) { ?>						<tr>							<td><a href="javascript:void(0)" onClick="loanPayment(<? echo $value['id']; ?>)"><? echo $value['code']; ?></a></td>							<td><a href="javascript:void(0)" onClick="loanCostumer(<? echo $value['costumer']; ?>)"><? echo $value['costumername']; ?></a></td>							<td>								<? if(($value['settled'] == 0) && (date('Y-m-d') >= date('Y-m-d', strtotime($value['deadline'])))) : ?> 									<span class='text-error'><? echo date('d-m-Y', strtotime($value['deadline'])); ?></span>								<? else: ?>									<span class="text-info"><? echo date('d-m-Y', strtotime($value['deadline'])); ?></span>								<? endif; ?>							</td>							<td><? echo $value['settled'] == 0 ? "Belum Lunas" : "Lunas"; ?></td>							<td><? echo $this->pecah($value['total']); ?></td>							<td><? echo $this->pecah($value['total']-$value['balance']); ?></td>							<td><? echo $this->pecah($value['balance']); ?></td>						</tr>					<? } ?>					</tbody>				</table>			</div>			<div class="pull-right">				<button class="btn btn-inverse" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputSearching').typeahead({			source : function(query,process){				return $.post(site+'loan/xlistloan', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].code +"#"+ response[i].costumername +"#"+ response[i].balance);					}					return process(sourceArr);				})			},			highlighter : function(item){				var loan = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/user.png'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + loan[2] + "</div>"                		+ "<div class='typeahead_secondary'>Nomor : #" + loan[1] + " | Sisa : " + formatRupiah(loan[3]) + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var loan = item.split('#');				load('report/loanpayment/'+loan[0],'#content');			}		});	})	function loanPayment(id)	{		window.location = site+"report/loanpayment/"+id;	}	function loanCostumer(costumer)	{		window.location = site+"report/loancostumer/"+costumer;	}</script>