<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">		<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>report/retail">Laporan Stock</a></h2>		</div>		<div class="well">			<input id="inputFilterFirstDate" class="span2" type="text" data-provide="datepicker" value="<? echo $this->firstDate; ?>"> S/D 			<input id="inputFilterLastDate" class="span2" type="text" data-provide="datepicker" value="<? echo $this->lastDate; ?>">						<div id="printArea">				<table style="border:none; width:100%;">					<tr>						<th colspan="3"><center>LAPORAN PENJUALAN SATUAN PERIODIK</center></th>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td width:'40%'>							<p>								TOKO BUKU TOHA PUTRA<br>								Cabang MAKASSAR<br>								Jl. Sultan Alauddin No. 18-19 Komp. Ruko Permata Sari<br>								Telp : 0411-868601, FAX : 0411-868177<br>							</p>						</td>						<td width:'20%'>						</td>						<td width:'40%'>						</td>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td>							<small>								PERIODE : 								<? echo date('d-m-Y', strtotime($this->firstDate)); ?> 								S/D 								<? echo date('d-m-Y', strtotime($this->lastDate)); ?>							</small>						</td>						<td></td>						<td><small class="pull-right">Tanggal Cetak : <? echo date('d-m-Y'); ?></small></td>					</tr>				</table>				<table class="table table-bordered">					<thead>						<tr>							<th width='30%' style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Nomor</th>							<th width='30%' style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Tanggal</th>							<th width='30%' style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right">Total Penjualan (Rp)</span></th>						</tr>					</thead>									<tbody>					<? $count = 0; $total = 0; 						foreach ($this->listRetail as $key => $value) { 							$count++; $total += $value['total']; ?>						<tr>							<td><? echo $count; ?></td>							<td><? echo date('d-m-Y', strtotime($value['selldate'])); ?></td>							<td><span class="pull-right"><? echo $this->pecahNoRp($value['total']); ?></span></td>						</tr>					<? } ?>						<tr>							<td colspan="2" style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right">Total Penjualan</span></td>							<td style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right"><? echo $this->pecahNoRp($total); ?></span></td>						</tr>					</tbody>				</table>			</div>			<div class="pull-right">				<button class="btn btn-inverse" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputFilterFirstDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').on('changeDate', function(){			filterByDate();		});	})	function filterByDate()	{		var first = $('#inputFilterFirstDate').val();		var last = $('#inputFilterLastDate').val();		if(first != '' && last != '')		{			load("report/retail/"+first+"_"+last,'#content');		}			}</script>