<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">		<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>report/retail">Laporan Penjualan</a></h2>		</div>		<div class="well">						<div id="printArea">				<div class="reportTitle">					<center>						<legend>TOKO BUKU TOHA PUTRA</legend>						<p>Cabang MAKASSAR</p>						<p>Alamat : Jl. Sultan Alauddin No. 18-19 Komp. Ruko Permata Sari</p>						<p>Telp : 0411-868601, FAX : 0411-868177</p>						<hr noshade size=7 width="100%">												<br><br>						<legend>LAPORAN PENJUALAN HARIAN TANGGAL : <? echo date('d-m-Y', strtotime($this->date)); ?></legend>						<hr width="100%">						<br>					</center>				</div>				<br>				<table class="table table-bordered">					<thead>						<tr>							<th width='30%'>Nomor</th>							<th width='30%'>Nomor Struk</th>							<th width='30%'>Total Penjualan</th>						</tr>					</thead>									<tbody>					<? $count = 0; $total = 0; 						foreach ($this->listRetail as $key => $value) { 							$count++; $total += $value['total']; ?>						<tr>							<td><? echo $count; ?></td>							<td><a href="javascript:void(0)" onclick="load('report/retaildetails/<? echo $value['id']; ?>','#content')">#<? echo $value['code']; ?></a></td>							<td><? echo $this->pecah($value['total']); ?></td>						</tr>					<? } ?>						<tr>							<td colspan="2"><span class="pull-right">Total Penjualan</span></td>							<td><strong><? echo $this->pecah($total); ?></strong></td>						</tr>					</tbody>				</table>			</div>			<div class="pull-right">				<button class="btn btn-inverse" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputFilterFirstDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').on('changeDate', function(){			filterByDate();		});	})	function filterByDate()	{		var first = $('#inputFilterFirstDate').val();		var last = $('#inputFilterLastDate').val();		if(first != '' && last != '')		{			load("report/retail/"+first+"_"+last,'#content');		}			}</script>