<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">		<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>report/wholesale">Laporan Penjualan Grosir</a></h2>		</div>		<div class="well">			<input id="inputFilterFirstDate" class="span2" type="text" data-provide="datepicker" value="<? echo $this->firstDate; ?>"> S/D 			<input id="inputFilterLastDate" class="span2" type="text" data-provide="datepicker" value="<? echo $this->lastDate; ?>">		</div>		<div class="well">			<div id="printArea">				<table style="border:none; width:100%;">					<tr>						<th colspan="3"><center>LAPORAN PENJUALAN GROSIR PERIODIK</center></th>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td width:'40%'>							<p>								TOKO BUKU TOHA PUTRA<br>								Cabang MAKASSAR<br>								Jl. Sultan Alauddin No. 18-19 Komp. Ruko Permata Sari<br>								Telp : 0411-868601, FAX : 0411-868177<br>							</p>						</td>						<td width:'20%'>						</td>						<td width:'40%'>						</td>					</tr>					<tr>						<td colspan="3"><br></td>					</tr>					<tr>						<td>							<small>								PERIODE : 								<? echo date('d-m-Y', strtotime($this->firstDate)); ?> 								S/D 								<? echo date('d-m-Y', strtotime($this->lastDate)); ?>							</small>						</td>						<td></td>						<td><small class="pull-right">Tanggal Cetak : <? echo date('d-m-Y'); ?></small></td>					</tr>				</table>				<table class="table table-bordered">					<thead>						<tr>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Nomor Transaksi</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Tanggal</th>							<th width="30%" style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Pelanggan</th>							<th style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;">Tunai/Kredit</th>							<th width="15%" style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right">Total (Rp)</span></th>							<th width="15%" style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right">Cash (Rp)</span></th>						</tr>					</thead>					<tbody>					<? $totalprice = 0; $totalcash = 0;					foreach ($this->listWholesale as $key => $value) { 						$totalprice += $value['total'];						if($value['cash'] == 1): 							$totalcash += $value['total'];						else:							$totalcash += $value['dp']; 						endif; ?>						<tr id="details-<? echo $value['id']; ?>">							<td><? echo $value['code']; ?></td>							<td><? echo date('d-m-Y', strtotime($value['selldate'])); ?></td>							<td><? echo $value['costumername']; ?></td>							<td><? echo $value['cash'] == 1 ? "Tunai" : "Kredit"; ?></td>							<td><span class="pull-right"><? echo $this->pecahNoRp($value['total']); ?></span></td>							<td><span class="pull-right"><? echo $value['cash'] == 0 ? $this->pecahNoRp($value['dp']) : $this->pecahNoRp($value['total']); ?></span></td>						</tr>					<? } ?>						<tr>							<td colspan="4" style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right">TOTAL  </span></td>							<td style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right"><? echo $this->pecahNoRp($totalprice); ?></span></td>							<td style="border-top: 1px solid #000 !important; border-bottom: 1px solid #000 !important;"><span class="pull-right"><? echo $this->pecahNoRp($totalcash); ?></span></td>						</tr>					</tbody>				</table>			</div>			<div class="pull-right">				<button class="btn btn-inverse" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputFilterFirstDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterLastDate').on('changeDate', function(){			filterByDate();		});	})	function filterByDate()	{		var first = $('#inputFilterFirstDate').val();		var last = $('#inputFilterLastDate').val();		if(first != '' && last != '')		{			load("report/wholesale/"+first+"_"+last,'#content');		}	}	function reportDetails(id)	{		window.location = site+"report/wholesaledetails/"+id;	}</script>