<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>retail">Penjualan Eceran</a></h2>		</div>		<div class="well">			<table class="table" style="border:none">				<tbody>					<tr>						<td>							Nomor Transaksi : 							<? echo $this->detailSelling['closing'] == 0 ? 											"<span class='text-error'><strong>#".$this->detailSelling['code']."</strong></span>" : 											"<span class='text-success'><strong>#".$this->detailSelling['code']."</strong></span>"; ?>						</td>						<td>Tgl Transaksi : <strong><? echo date('d-m-Y', strtotime($this->detailSelling['selldate'])); ?></strong></td>						<td><span class="pull-right"><strong>Kasir : <? echo $this->detailSelling['employeename']; ?></strong></span></td>					</tr>				</tbody>			</table>			<input type="hidden" id="selling" value="<? echo $this->detailSelling['id']; ?>">			<table class="table table-bordered table-hover">				<thead>					<tr>						<th width='10%'>Nomor</th>						<th width='50%'>Judul / Nama Produk</th>						<th>Harga Pokok</th>						<th>Jumlah</th>						<th>Diskon</th>						<th>Harga Jual</th>						<th>Sub Total</th>					</tr>				</thead>								<tbody id="tblDetails">					<? $count = 0; $totalprice = 0;					foreach ($this->listTransaction as $key => $value) { 						$count++; 						$totalprice += $value['total']*$value['sellprice']; ?>						<tr id="details-<? echo $value['id']; ?>">							<td><? echo $count; ?></td>							<td><? echo $value['title']; ?></td>							<td><span class="pull-right"><? echo $this->pecah($value['price']); ?></td>							<td><? echo $value['total']; ?></td>							<td><? echo ($value['discount']/$value['price'])*100; ?></td>							<td><span class="pull-right"><? echo $this->pecah($value['sellprice']); ?></span></td>							<td><span class="pull-right"><? echo $this->pecah($value['sellprice']*$value['total']); ?></span></td>						</tr>					<? } ?>						<tr>							<td colspan="5"><span class="pull-right">Total Harga</span></td>							<td colspan="2"><span class="pull-right"><? echo $this->pecah($totalprice); ?></span></td>						</tr>				</tbody>			</table>			<div class="pull-right">				<? if($this->detailSelling['closing'] == 0) : ?>					<button class="btn btn-primary" onClick="load('retail/newtransaction/'+<? echo $this->detailSelling['id']; ?>, '#content')">						<i class="icon-pen icon-white"></i> Lanjutkan Transaksi					</button>					<button class="btn btn-inverse" onClick="showFormPayment()"><i class="icon-print icon-white"></i> Bayar</button>				<? else: ?>					<button class="btn btn-inverse" onClick="showFormRePayment()"><i class="icon-print icon-white"></i> Cetak Ulang</button>				<? endif; ?>			</div>			<br><br>		</div>	</div></div><!-- Form Payment [modal]===================================== --><div id="formPayment" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Pembayaran</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputPay">Bayar</label>				<div class="controls">					<input id="inputPay" class="span2" type="text" onkeyup="formatNumber(this)">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="closing()" data-dismiss="modal" aria-hidden="true">Bayar</button>	</div></div><!-- End of Form Payment [modal] --><!-- Form RePayment [modal]===================================== --><div id="formRePayment" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Pembayaran</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputRePay">Bayar</label>				<div class="controls">					<input id="inputRePay" class="span2" type="text" onkeyup="formatNumber(this)">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="reClosing()" data-dismiss="modal" aria-hidden="true">Bayar</button>	</div></div><!-- End of Form Payment [modal] --><script type="text/javascript">	function showFormPayment()	{		$('#formPayment').modal('show');	}	function showFormRePayment()	{		$('#formRePayment').modal('show');	}	function closing()	{		var selling = $('#selling').val();		var payment = parseInt(replaceAll($('#inputPay').val(),',',''));		window.location = site+"retail/closing/"+selling+"_"+payment;	}	function reClosing()	{		var selling = $('#selling').val();		var payment = parseInt(replaceAll($('#inputRePay').val(),',',''));		window.location = site+"retail/reclosing/"+selling+"_"+payment;	}</script>