<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>retail">Penjualan Eceran</a></h2>		</div>		<div class="well">			<div class="pull-right"> 				<button class="btn btn-success" onClick="addNew()">Transaksi Baru</button> 			</div>			<input id="inputFilterDate" class="span2" type="text" data-provide="datepicker" value="<? echo date('Y-m-d'); ?>">			<input type="hidden" id="inputEmployeeID" value="<? echo Session::get('user_employee'); ?>">			<br><br>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Nomor</th>						<th>Nomor Transaksi</th>						<th>Total Harga</th>						<th width='10%'>Aksi</th>					</tr>				</thead>								<tbody id="tbltransaction">					<? $count = 0; $total = 0;					foreach ($this->listTransaction as $key => $value) { 						$count++;  						$value['closing'] == 1 ? $total += $value['total'] : $total += 0; ?>						<tr id="details-<? echo $value['id']; ?>">							<td><? echo $count; ?></td>							<td>								<? echo $value['closing'] == 0 ? 										"<span class='text-error'><strong>#".$value['code']."</strong></span>" : 										"<span class='text-success'><strong>#".$value['code']."</strong></span>"; ?>							</td>							<td><span class="pull-right"><? echo $this->pecah($value['total']); ?></span></td>							<td>								<div class="btn-group">									<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return selectThis(<? echo $value['sellingid']; ?>)">											<i class="icon-pencil"></i> Lihat Detail</a>										</li>										<? if($value['closing'] == 0) : ?>											<li>												<a href="javascript:void(0)" onClick="load('retail/newtransaction/'+<? echo $value['sellingid']; ?>,'#content');">												<i class="icon-ok"></i> Lanjutkan Transaksi</a>											</li>										<? endif; ?>									</ul>								</div>							</td>						</tr>					<? } ?>						<tr>							<td colspan="2"><span class="pull-right"><strong>Total Penjualan</strong></span></td>							<td><span class="pull-right"><strong><? echo $this->pecah($total); ?></strong></span></td>							<td></td>						</tr>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputFilterDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});		$('#inputFilterDate').on('changeDate', function(){			filterByDate();		});	})	function addNew()	{		var employee = $('#inputEmployeeID').val();		var selldate = $('#inputFilterDate').val();		$.post(site+'retail/newselling',{employee : employee, selldate : selldate}, function(data){			load('retail/newtransaction/'+data.selling,'#content');		},'json');	}	function selectThis(id)	{		load('retail/details/'+id,'#content');	}	function filterByDate()	{		var date = $('#inputFilterDate').val();		if(date)		{			load('retail/filterbydate/'+date,'#tbltransaction');		}	}</script>