<!-- Main Row =============================================== --><div class="container-fluid">    <div class="row-fluid">		        <div class="well">            <h2 class="text-info"><a href="<? echo URL; ?>returnment">Retur</a></h2>        </div>        <div class="well">            <div class="pull-right">                <button class="btn btn-success" onClick="showFormAddNew()"><i class="icon-plus icon-white"></i> Buat Retur</button>            </div>            <br><br>            <table class="table table-bordered table-hover">                <thead>                    <tr>                        <th width="10%">Nomor</th>                        <th width="15%">Nomor Retur</th>                        <th width="40%">Supplier</th>                        <th width="15%">Tanggal Pengembalian</th>                        <th width='10%'>Aksi</th>                    </tr>                </thead>                <tbody id="tblReturnment">                    <? $count = 0;                    foreach ($this->listReturnment as $key => $value) {                         $count++; ?>                        <tr id="returnment-<? echo $value['id']; ?>">                            <td><? echo $count; ?></td>                            <td>#<? echo $value['code']; ?></td>                            <td><? echo $value['suppliername']; ?></td>                            <td><? echo date('d-m-Y', strtotime($value['returndate'])); ?></td>                            <td>                                <div class="btn-group">                                    <a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>                                    <a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>                                    <ul class="dropdown-menu">                                        <li>                                            <a href="javascript:void(0)" onClick="load('returnment/details/'+<? echo $value['id']; ?>,'#content');">                                            <i class="icon-pencil"></i> Lihat Detail</a>                                        </li>                                        <li>                                            <a href="javascript:void(0)" onClick="load('returnment/transactions/'+<? echo $value['id']; ?>,'#content');">                                            <i class="icon-ok"></i> Lanjutkan Transaksi</a>                                        </li>                                    </ul>                                </div>                            </td>                        </tr>                    <? } ?>                </tbody>            </table>        </div>    </div></div><!-- Form Add Returnment [modal]===================================== --><div id="formAddNew" class="modal hide fade" tabindex="-1" aria-hidden="true">    <div class="modal-header">        <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>        <h3 id="myModalLabel">Retur Baru</h3>    </div>    <div class="modal-body">        <form class="form-horizontal">            <div class="control-group">                <label class="control-label" for="inputTitle">Supplier</label>                <div class="controls">                    <input id="inputSupplierName" class="span3" type="text" data-provide="typeahead" placeholder="Cari Supplier">                    <input id="inputSupplierID" type="hidden" value="0">                </div>            </div>            <div class="control-group">                <label class="control-label" for="inputReturnDate">Tanggal Retur</label>                <div class="controls">                    <input id="inputReturnDate" class="span2" type="text" data-provide="datepicker" value="<? echo date('Y-m-d'); ?>">                </div>            </div>        </form>    </div>    <div class="modal-footer">        <button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>        <button class="btn btn-primary" onClick="addNew()" data-dismiss="modal" aria-hidden="true">Simpan</button>    </div></div><!-- End of Form Add Returnment [modal] --><script type="text/javascript">    $(function(){        $('#inputReturnDate').datepicker({format: 'yyyy-mm-dd', autoclose : true});        $('#inputSupplierName').typeahead({            source : function(query,process){                return $.post(site+'supplier/xlistsupplier', {param : query}, function(response){                    response = $.parseJSON(response);                    var sourceArr = [];	                    for(var i = 0; i < response.length; i++)                    {                        sourceArr.push(response[i].id +"#"+ response[i].name);                    }                    return process(sourceArr);                })            },            highlighter : function(item){                var supplier = item.split('#');                var item = ''                + "<div class='typeahead_wrapper'>"                + "<img class='typeahead_photo' src='"+site+"assets/img/user.png'/>"                + "<div class='typeahead_labels'>"                + "<div class='typeahead_primary'>" + supplier[1] + "</div>"                + "</div>"                + "</div>";            return item;            },            updater : function(item){                var supplier = item.split('#');                $('#inputSupplierID').val(supplier[0]);                return supplier[1];            }        });    })    function showFormAddNew()    {        $('#formAddNew').modal('show');    }    function addNew()    {        var returndate = $('#inputReturnDate').val();        var supplier = $('#inputSupplierID').val();        $.post(site+'returnment/addnew',{supplier : supplier, returndate : returndate}, function(data){            load('returnment/transactions/'+data.id,'#content');        },'json');    }</script>