<!-- Main Row =============================================== --><div class="container-fluid">    <div class="row-fluid">        <div class="well">            <h2 class="text-info"><a href="<? echo URL; ?>returnment">Retur</a></h2>        </div>        <div class="well">            <div class="pull-right">                <button class="btn btn-success" onClick="showFormAddProduct()"><i class="icon-plus icon-white"></i> Tambahkan Item</button>            </div>            <br><br>            <table class="table" style="border:none">                <tr>                    <td>Nomor Retur : <strong>#<? echo $this->returnmentDetails['code']; ?></strong></td>                    <td>Supplier : <strong><? echo $this->returnmentDetails['suppliername']; ?></strong></td>                    <td><span class="pull-right">Tgl Transaksi : <strong><? echo date('d-m-Y', strtotime($this->returnmentDetails['returndate'])); ?></strong></span></td>                </tr>            </table>            <input type="hidden" id="returnment" value="<? echo $this->returnmentDetails['id']; ?>">            <input type="hidden" id="supplier" value="<? echo $this->returnmentDetails['supplier']; ?>">            <input type="hidden" id="returndate" value="<? echo $this->returnmentDetails['returndate']; ?>">            <table class="table table-bordered table-hover">                <thead>                    <tr>                        <th width='10%'>Nomor</th>                        <th width='15%'>Kode</th>                        <th width='60%'>Judul / Nama Produk</th>                        <th>Jumlah</th>                        <th width='10%'>Aksi</th>                    </tr>                </thead>                <tbody id="tblDetails">                    <? $count = 0;                    foreach ($this->listDetails as $key => $value) {                         $count++; ?>                        <tr id="details-<? echo $value['id']; ?>">                            <td><? echo $count; ?></td>                            <td><? echo $value['code']; ?></td>                            <td><? echo $value['title']; ?></td>                            <td><? echo $value['quantity']; ?></td>                            <td>                                <div class="btn-group">                                    <a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>                                    <a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>                                    <ul class="dropdown-menu">                                        <li>                                            <a href="javascript:void(0)" onClick="return deleteProduct(<? echo $value['id']; ?>)">                                            <i class="icon-remove"></i> Batalkan</a>                                        </li>                                    </ul>                                </div>                            </td>                        </tr>                    <? } ?>                </tbody>            </table>            <div class="pull-right">                <button class="btn btn-inverse" onClick="load('returnment/printDetails/'+<? echo $this->returnmentDetails['id']; ?>,'#content')"><i class="icon-print icon-white"></i> Cetak Retur</button>            </div>            <br><br>        </div>    </div></div><!-- Form Add Product [modal]===================================== --><div id="formAddProduct" class="modal hide fade" tabindex="-1" aria-hidden="true">    <div class="modal-header">        <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>        <h3 id="myModalLabel">Tambah Item</h3>    </div>    <div class="modal-body">        <form class="form-horizontal">            <div class="control-group">                <label class="control-label" for="inputName">Judul / Nama</label>                <div class="controls">                    <input id="inputProductID" type="hidden" value="0">                    <input id="inputTitle" class="span3" type="text" data-provide="typeahead" placeholder="Cari Judul/Item">                </div>            </div>	            <div class="control-group">                <label class="control-label" for="inputQuantity">Jumlah</label>                <div class="controls">                    <input id="inputQuantity" type="text" class="span1">                </div>            </div>        </form>    </div>    <div class="modal-footer">        <button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>        <button class="btn btn-primary" onClick="addProduct()" data-dismiss="modal" aria-hidden="true">Simpan</button>    </div></div><!-- End of Form Add Product [modal] --><script type="text/javascript">    $(function(){        $('#inputDeadline').datepicker({format: 'yyyy-mm-dd', autoclose : true});        $('#inputTitle').typeahead({            source : function(query,process){                return $.post(site+'product/xlistproductfiltersupplier', {title : query, supplier : $('#supplier').val()}, function(response){                    response = $.parseJSON(response);                    var sourceArr = [];	                    for(var i = 0; i < response.length; i++)                    {                        sourceArr.push(response[i].id +"#"+ response[i].title +"#"+ response[i].sellprice);                    }                    return process(sourceArr);                })            },            highlighter : function(item){                var product = item.split('#');                var item = ''                + "<div class='typeahead_wrapper'>"                + "<img class='typeahead_photo' src='"+site+"assets/img/books.png'/>"                + "<div class='typeahead_labels'>"                + "<div class='typeahead_primary'>" + product[1] + "</div>"                + "</div>"                + "</div>";            return item;            },            updater : function(item){                var product = item.split('#');                $('#inputProductID').val(product[0]);                $('#inputPrice').val(product[2]);                return product[1];            }        });    })    function showFormAddProduct()    {        $('#formAddProduct').modal('show');        $('#inputProductID').val('0');        $('#inputTitle').val('');    }    function addProduct()    {        var product = $('#inputProductID').val();        var quantity = $('#inputQuantity').val();        var returnment = $('#returnment').val();        $.post(site+'returnment/addproduct',             {returnment : returnment,                product : product,                quantity : quantity},             function(){                load('returnment/transactions/'+returnment, '#content');        });    }    function deleteProduct(id)    {        if(confirm('Yakin Akan Menghapus?!'))        {            $.post(site+'returnment/deleteproduct',{id : id}, function(){                $('#details-'+id).remove();            });        }    }</script>