<? foreach ($this->listSupply as $key => $value) { ?>	<tr id="supply-<? echo $value['id']; ?>">		<td><? echo $value['code']; ?></td>		<td><? echo $value['title']; ?></td>		<td><? echo date('d-m-Y', strtotime($value['supplydate'])); ?></td>		<td><? echo $value['total']; ?></td>		<td>			<div class="btn-group">				<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return selectThis(<? echo $value['id']; ?>)">						<i class="icon-pencil"></i> Detail Produk</a>						<a href="javascript:void(0)" onClick="return removeThis(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Batalkan</a>					</li>				</ul>			</div>		</td>	</tr><? } ?>