<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h2 class="text-info"><a href="<? echo URL; ?>wholesale">Penjualan Grosir</a></h2>		</div>		<div class="well">			<table class="table" style="border:none">				<tr>					<td>						Nomor Transaksi : 						<? echo $this->detailSelling['closing'] == 0 ? 										"<span class='text-error'><strong>#".$this->detailSelling['code']."</strong></span>" : 										"<span class='text-success'><strong>#".$this->detailSelling['code']."</strong></span>"; ?>					</td>					<td><span class="pull-right">Tgl Transaksi : <strong><? echo date('d-m-Y', strtotime($this->detailSelling['selldate'])); ?></strong></span></td>				</tr>				<tr>					<td>Pelanggan : <strong><? echo $this->detailSelling['costumername']; ?></strong></td>					<td><span class="pull-right">Kasir : <strong><? echo $this->detailSelling['employeename']; ?></strong></span></td>				</tr>			</table>			<input type="hidden" id="selling" value="<? echo $this->detailSelling['id']; ?>">			<table class="table table-bordered table-hover">				<thead>					<tr>						<th width='10%'>Nomor</th>						<th width='50%'>Judul / Nama Produk</th>						<th>Jumlah</th>						<th>Diskon</th>						<th>Harga Jual</th>						<th>Sub Total</th>						<th>Aksi</th>					</tr>				</thead>								<tbody id="tblDetails">					<? $count = 0; $totalprice = 0;					foreach ($this->listTransaction as $key => $value) { 						$count++; 						$totalprice += $value['total']*$value['sellprice']; ?>						<tr id="details-<? echo $value['id']; ?>">							<td><? echo $count; ?></td>							<td><? echo $value['title']; ?></td>							<td><? echo $value['total']; ?></td>							<td><? echo floor(($value['discount']/$value['price'])*100); ?>%</td>							<td><? echo $this->pecah($value['sellprice']); ?></td>							<td><? echo $this->pecah($value['sellprice']*$value['total']); ?></td>							<td>								<? if($this->detailSelling['closing'] == 0) : ?>									<button class="btn btn-small btn-danger" onClick="return deleteProduct(<? echo $value['id']; ?>)">Batalkan</button>								<? endif; ?>							</td>						</tr>					<? } ?>						<tr>							<td colspan="5"><span class="pull-right">Total Harga</span></td>							<td colspan="2"><? echo $this->pecah($totalprice); ?></td>						</tr>				</tbody>			</table>			<div class="pull-right">				<? if($this->detailSelling['closing'] == 0) : ?>					<button class="btn btn-primary" onClick="load('wholesale/newtransaction/'+<? echo $this->detailSelling['id']; ?>, '#content')">						<i class="icon-pen icon-white"></i> Lanjutkan Transaksi					</button>					<button class="btn btn-inverse" onClick="closing()">						<i class="icon-shopping-cart icon-white"></i> Tunai/Cetak Faktur					</button>					<button class="btn btn-inverse" onClick="showFormPaymentwithDebt()">						<i class="icon-comment icon-white"></i> Kredit/Terbitkan Invoice					</button>				<? else: ?>						<? if($this->detailSelling['cash'] == 0) : ?>						<button class="btn btn-inverse" onClick="reInvoice()"><i class="icon-print icon-white"></i> Cetak Invoice</button>					<? else: ?>						<button class="btn btn-inverse" onClick="reClosing()"><i class="icon-print icon-white"></i> Cetak Faktur</button>					<? endif; ?>				<? endif; ?>			</div>			<br><br>		</div>	</div></div><!-- Form Payment with Debt [modal]===================================== --><div id="formPaymentwithDebt" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Pembayaran DP</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputDownPay">Down Payment</label>				<div class="controls">					<input id="inputDownPay" class="span2" type="text" value="0" onkeyup="formatNumber(this)">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputDeadline">Batas Akhir Pelunasan</label>				<div class="controls">					<input id="inputDeadline" class="span2" type="text" data-provide="datepicker" value="<? echo date('Y-m-d'); ?>">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="releaseLoan()" data-dismiss="modal" aria-hidden="true">Bayar</button>	</div></div><!-- End of Form Payment with Debt [modal] --><script type="text/javascript">	function showFormPayment()	{		$('#formPayment').modal('show');	}	function showFormRePayment()	{		$('#formRePayment').modal('show');	}	function showFormPaymentwithDebt()	{		$('#formPaymentwithDebt').modal('show');	}	function deleteProduct(id)	{		if(confirm("Anda Yakin Akan Menghapus Item Ini?"))		{			$.post(site+'wholesale/deletedetails', {id : id}, function(){				$('#details-'+id).remove();			});		}	}	function closing()	{		var selling = $('#selling').val();		window.location = site+"wholesale/payment/"+selling;	}	function releaseLoan()	{		var selling = $('#selling').val();		var costumer = $('#costumer').val();		var employee = $('#employee').val();		var downpay = parseInt(replaceAll($('#inputDownPay').val(),',',''));		var totalprice = $('#totalprice').val();		var deadline = $('#inputDeadline').val();		var selldate = $('#selldate').val();		$.post(site+'loan/addnew', 			{selling : selling,			costumer : costumer,			employee : employee,			totalprice : totalprice,			downpay : downpay,			deadline : deadline,			selldate : selldate}, function(data){				window.location = site+"wholesale/invoice/"+selling+"_"+data.loan+"_"+downpay;		},'json');	}	function reInvoice()	{		var selling = $('#selling').val();		window.location = site+"wholesale/reinvoice/"+selling;	}	function reClosing()	{		var selling = $('#selling').val();		window.location = site+"wholesale/repayment/"+selling;	}</script>