<? $count = 0; $totalprice = 0; $totalcash = 0;foreach ($this->listTransaction as $key => $value) { 	$count++;	$totalprice += $value['total'];	if($value['cash'] == 1): 		$totalcash += $value['total'];	else:		$totalcash += $value['dp']; 	endif; ?>	<tr id="details-<? echo $value['id']; ?>">		<td><? echo $count; ?></td>		<td>			<? if($value['closing'] == 0) { ?>				<span class='text-error'>					<strong>						<a href='javascript:void(0)' onClick="load('wholesale/details/<? echo $value['sellingid']; ?>','#content')">							#<? echo $value['code'] ?>						</a>					</strong>				</span> 			<? }else{ ?>				<span class='text-success'>					<strong>						<a href='javascript:void(0)' onClick="load('wholesale/details/<? echo $value['sellingid']; ?>','#content')">							#<? echo $value['code']; ?>						</a>					</strong>				</span> 			<? } ?>		</td>		<td><strong><? echo $value['costumername']; ?></strong></td>		<td><? echo $value['cash'] == 1 ? "Tunai" : "Kredit"; ?></td>		<td><? echo $this->pecah($value['total']); ?></td>		<td>			<div class="btn-group">				<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return selectThis(<? echo $value['sellingid']; ?>)">						<i class="icon-pencil"></i> Lihat Detail</a>					</li>					<? if($value['closing'] == 0) : ?>						<li>							<a href="javascript:void(0)" onClick="load('wholesale/newtransaction/'+<? echo $value['sellingid']; ?>,'#content');">							<i class="icon-ok"></i> Lanjutkan Transaksi</a>						</li>					<? endif; ?>				</ul>			</div>		</td>	</tr><? } ?>	<tr>		<td colspan="4"><span class="pull-right">Total Penjualan</span></td>		<td><strong><? echo $this->pecah($totalprice); ?></strong></td>		<td></td>	</tr>	<tr>		<td colspan="4"><span class="pull-right">Total Cash Masuk</span></td>		<td><strong><? echo $this->pecah($totalcash); ?></strong></td>		<td></td>	</tr>