<? $count = 0; $totalprice = 0;foreach ($this->listTransaction as $key => $value) { 	$count++; 	$totalprice += $value['total']*$value['sellprice']; ?>	<tr id="details-<? echo $value['id']; ?>">		<td><? echo $count; ?></td>		<td><? echo $value['title']; ?></td>		<td><? echo $this->pecah($value['price']); ?></td>		<td><? echo $value['total']; ?></td>		<td><? echo floor(($value['discount']/$value['price'])*100); ?>%</td>		<td><? echo $this->pecah($value['sellprice']); ?></td>		<td><? echo $this->pecah($value['sellprice']*$value['total']); ?></td>		<td>			<div class="btn-group">				<a class="btn btn-info" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-info dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return deleteProduct(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Batalkan</a>					</li>				</ul>			</div>		</td>	</tr><? } ?>	<tr>		<td colspan="5"><span class="pull-right">Total Harga</span></td>		<td colspan="2"><? echo $this->pecah($totalprice); ?><input type="hidden" id="totalprice" value="<? echo $totalprice; ?>"></td>		<td></td>	</tr>