<?phpclass View{	function __construct()	{			}		public function render($name, $noInclude = false)	{		if($noInclude == true)		{			require APP . 'views/'. $name .'.php';		}		else		{			require APP . 'views/header.php';            require APP . 'views/'. $name .'.php';            require APP . 'views/footer.php';		}	}	function pecah($uang,$delimiter='.')    {        if($uang == '' || $uang == 0)        {            $rupiah = '0,00';            return $rupiah;        }        $neg = false;        if($uang<0)        {            $neg = true;            $uang = abs($uang);        }                $rupiah = number_format($uang,2,',',$delimiter);        $rupiah = 'Rp'.$rupiah;        return $rupiah;    }    function pecahNoRp($uang,$delimiter='.')    {        if($uang == '' || $uang == 0)        {            $rupiah = '0,00';            return $rupiah;        }        $neg = false;        if($uang<0)        {            $neg = true;            $uang = abs($uang);        }                $rupiah = number_format($uang,2,',',$delimiter);        $rupiah = $rupiah;        return $rupiah;    }}